import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import classnames from 'classnames';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import red from '@material-ui/core/colors/red';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import {ReactComponent as PendingIcon} from '../Icons/pending.svg';
import {ReactComponent as LawyerRejectedIcon} from '../Icons/lawyerrejected.svg';
import {ReactComponent as AcceptedIcon} from '../Icons/accepted.svg';
import {ReactComponent as ReviewerRejectedIcon} from '../Icons/reviewerrejected.svg';
import CustomDialog from '../layout/Dialogs/CustomDialog';

const styles = theme => ({
  card: {
    maxWidth: 400,
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  actions: {
    display: 'flex',
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: '#fafafa'
  }
});

class InvestorCard extends React.Component {
  state = { expanded: false };

  handleExpandClick = () => {
    this.setState(state => ({ expanded: !state.expanded }));
  };

  checkStatusIcon = props => {
    console.log(props.company)
    switch(props.company.status){
      case 'PendingLawyer': return <PendingIcon />;
      case 'PendingReviewer': return <PendingIcon /> ;
      case 'RejectedLawyer': return <LawyerRejectedIcon />;
      case 'RejectedReviewer': return <ReviewerRejectedIcon />;
      case 'Accepted': return  <AcceptedIcon />;
    }
  }

  reformatStatus = props => {
    switch(props.company.status){
      case 'PendingLawyer' : return 'Pending Lawyer';
      case 'PendingReviewer': return 'Pending Reviewer';
      case 'RejectedLawyer': return 'Rejected Lawyer';
      case 'RejectedReviewer': return 'Rejected Reviewer';
      case 'Accepted': return 'Accepted';
    }
  }

  render() {
    const { classes } = this.props;

    return (
      <Card className={classes.card}>
        <CardHeader
          avatar={
            <Avatar className={classes.avatar}>
              {this.checkStatusIcon(this.props)}
            </Avatar>
          }
          action={
            <IconButton>
              <MoreVertIcon />
            </IconButton>
          }
          title={this.props.company.nameInEnglish}
          subheader= {this.reformatStatus(this.props)}
        />
        <CardContent>
          <Typography variant="h10">
          {this.props.company.fees === undefined? 'Fees will be displayed here.' : `${this.props.company.fees} ${this.props.company.capitalCurrency}`}
          </Typography>
        </CardContent>
        <CardActions className={classes.actions} disableActionSpacing>
          <IconButton aria-label="Add to favorites">
            <CustomDialog className={classes.dialog} company={this.props.company} />
          </IconButton>
        </CardActions>
        <Collapse in={this.state.expanded} timeout="auto" unmountOnExit>
        </Collapse>
      </Card>
    );
  }
}

InvestorCard.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(InvestorCard);
