import React, { Component } from "react";
import SimpleCard from "../cards/SimpleCard2";
import SimpleCard2 from "../cards/SimpleCard2";

class ViewCompanies extends Component {
  constructor(props) {
    super(props);
    this.state = {
      investorCompanies: []
    };
  }

  componentDidMount() {
    fetch(`localhost:3000/api/investor/ViewCompanies`, {
      headers: {
        "x-access-token": this.props.token
      }
    })
      .then(res => res.json())
      .then(json => {
        this.setState({ investorCompanies: json.data });
      });
  }

  render() {
    return (
        <ul>
            {
                this.state.investorCompanies.map((item, i) => {
                    <SimpleCard2 key= {i} company={item}>
                    </SimpleCard2>
                })
            }
        </ul>
    )
  }
}
export default withStyles(styles)(ReviewerComment)
